from collections import defaultdict

def solution(inputs):
    s1, s2 = [x.split(" ") for x in inputs.splitlines()]
    conv = defaultdict(int)
    for m1 in s1:
        for m2 in s2:
            diff = round(float(m1) - float(m2), 5)
            conv[diff] += 1
    # print(conv)
    print(max(conv.values()))
    print(max(conv, key = conv.get))

